import React,{useState,useEffect} from 'react'
import { useDispatch, useSelector } from "react-redux";
import './Home.css'
import axios from 'axios'
function Home() {
    const products = useSelector((state) => state.product.products);
    const[subject,setSubjects]=useState([])
    const[lecturer,setLecturers]=useState('')


    useEffect(() => {

      let  flag = true
       axios.get("http://localhost:3001/subjects", )
           .then((res) => {
               if (flag) {
                setSubjects(res.data);
               }
           })


       return () => flag = false
   }, [subject])

    return (
        <div className='home'>

                <div className="home__top">
                    <h2>Learning Course</h2>
                    <table>
                <tbody>
                <tr>
                    <th className='title'>Your Courses</th>
                </tr>
            <tr >
                <th>Subject</th>
                <th>Lecturer</th>
                <th>Grade</th>
            </tr>
            {subject.map((item)=>{
                return(

            <tr key={item._id}>
                <td className='Course'>{item.subject}</td>
                <td className='Lecturers'>{item.lecturer}</td>
                <td className='Grade'>F</td>
            </tr>


                )
            })}
            </tbody>

            </table>
            </div>

        </div>
    )
}

export default Home
