import React,{useState,useEffect} from 'react'
import './StaffRoom.css'
import axios from 'axios'
import Avatar from '@material-ui/core/Avatar';
import {useSelector} from 'react-redux'
function StaffRoom() {
    const userLogin = useSelector((state) => state.userLogin);
    const { userInfo } = userLogin
    const[students,setStudents]=useState([])
    const[secret,setSecret] = useState('')
    const[message,setMessage] = useState('')

    useEffect(() => {

      let  flag = true
       axios.get("http://localhost:3001/getStudents", )
           .then((res) => {
               if (flag) {
                setStudents(res.data);
               }
           })


       return () => flag = false
   }, [])

   const auth =()=>{

       if(secret === "secretkey"){
        axios.put(`http://localhost:3001/access/${userInfo?._id}`,{
            role:"Lecturer"
        })
       }else{
           setMessage("Secret key is invalid.")
       }

   }
   console.log(userInfo?.role)
    return (
        <>
        {
            userInfo?.role === "Lecturer" ? (
            <div className='staffroom'>
            {students?.map((student)=>{
                return (
                    <div key={student._id} className='staffroom__cont'>
                        <div className="staffroom__students__box">
                            <Avatar style={{width:'50px',height:'50px'}} src={student?.pic} />
                                <div className="staffroom__students__info">
                                    <h3>{student?.username}</h3>
                                    <h4>{student?.email}</h4>
                                </div>
                        </div>
                        </div>
                )
            })}

        </div>)
        :
        (
        <div className='auth'>
            <h1>You don't have permissions!</h1>
            <h2>Type a secret key to unlock</h2>
            <input onChange={(e)=>setSecret(e.target.value)} type="text" placeholder='Secret key...' />
            <button onClick={auth}>Unlock</button>
        </div>
        )}

        </>
    )
}

export default StaffRoom
