import React,{useState,useEffect} from 'react'
import './StaffRoom.css'
import axios from 'axios'

function StaffRoom() {

    const[subject,setSubjects]=useState([])


    useEffect(() => {

      let  flag = true
       axios.get("http://localhost:3001/subjects", )
           .then((res) => {
               if (flag) {
                setSubjects(res.data);
               }
           })


       return () => flag = false
   }, [])
    return (
        <div className='staffroom'>

        <div className="staffroom__action">
        <h3>Add subject grade</h3>
            <div className="staffroom__box">
                <h3>Computer Skills</h3>
                  <div className="staffroom__subject">
                    <input type="number" />
                    <button type="submit">Add</button>
                  </div>
                <input type="number" />
                <button type="submit">Add</button>
            </div>
        </div>

 <table>
      <tbody>
                <tr>
                    <th className='title'>Your Courses</th>
                </tr>
            <tr >
                <th>Subject</th>
                <th>Lecturer</th>
                <th>Grade</th>
            </tr>
            {subject.map((item)=>{
                return(

            <tr key={item._id}>
                <td className='Course'>{item.subject}</td>
                <td className='Lecturers'>{item.lecturer}</td>
                <td className='Grade'>F</td>
            </tr>


                )
            })}
            </tbody>

            </table>
        </div>
    )
}

export default StaffRoom
