import { ActionTypes } from "./actionType";
const intialState = {
  products: [],

};

export const productsReducer = (state = intialState, { type, payload }) => {

  switch (type) {
    case ActionTypes.SET_PRODUCTS:

      return {

        ...state,

        products: [...state.products, payload],
      }

    default:
      return state;
  }
};
